import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import CustomNavbar from '../components/CustomNavbar';
import axios from 'axios';
import officelogIcon from '../images/officelog.png'; // Replace with the actual icon path
import resistanceIcon from '../images/resistance.png';
import aerobicIcon from '../images/aerobic.png';
import Footer from './Footer';

export default function History({ user }) {
  const userId = user._id;
  // console.log(userId);

  const [notesData, setnotesData] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);
  const [displayedItems, setDisplayedItems] = useState(10);

  useEffect(() => {
    const fetchnotesData = async () => {
      try {
        const activityResponse = await axios.get(`https://fitnesslogger-backend.onrender.com/aerobic/getAerobic/${userId}`);
        // console.log('activity notes Data:', activityResponse.data);

        const expenseResponse = await axios.get(`https://fitnesslogger-backend.onrender.com/resistance/getResistance/${userId}`);
        // console.log('Expense notes Data:', expenseResponse.data);

        const officelogResponse = await axios.get(`https://fitnesslogger-backend.onrender.com/officelog/getOfficelog/${userId}`);
        // console.log('Officelog notes Data:', officelogResponse.data);

        const combinednotesData = [
          ...(activityResponse.data || []),
          ...(Array.isArray(expenseResponse.data) ? expenseResponse.data : [expenseResponse.data]),
          ...(Array.isArray(officelogResponse.data) ? officelogResponse.data : [officelogResponse.data]),
        ];

        setnotesData(combinednotesData);
        setIsLoading(false);
      } catch (error) {
        console.error('Error fetching notes data:', error);
        setError(error);
        setIsLoading(false);
      }
    };

    fetchnotesData();
  }, [userId]);

  function showMoreItems() {
    setDisplayedItems(displayedItems + 3);
  }

  if (isLoading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div>Error: {error.message}</div>;
  }

  return (
    <div className="history">
      <div className="history-page">
        <CustomNavbar style={{ color: 'blue' }} />

        <div className="d-flex flex-column align-items-center">
          <h2 className="title">History</h2>
          {notesData.length ? (
            <div className="history-data">
              {notesData.slice(0, displayedItems).map((notes) => {
                let dateToDisplay = notes.date;
                return (
                  <div className="history-div d-flex" key={notes._id}>
                    <div className="date d-flex align-items-center">{dateToDisplay}</div>
                    <Link className="text-decoration-none" to={`/history/${notes.type}/${notes._id}`}>
                      {notes.type === 'officelog' ? (
                        <div className="history-card officelog-title d-flex">
                          <div className="d-flex align-items-center">
                            <img alt="officelog" src={officelogIcon} className="history-icon" />
                          </div>
                          <div>
                            <p className="history-name">{notes.name}</p>
                            {/* Replace with the appropriate property */}
                            <p className="history-index">{notes.property} units</p>
                          </div>
                        </div>
                      ) : notes.type === 'resistance' ? (
                        <div className="history-card expense-title d-flex">
                          <div className="d-flex align-items-center">
                            <img alt="expense" src={resistanceIcon} className="history-icon" />
                          </div>
                          <div>
                            <p className="history-name">{notes.name}</p>
                            <p className="history-index">{notes.weight} lbs</p>
                          </div>
                        </div>
                      ) : notes.type === 'aerobic' ? (
                        <div className="history-card activity-title d-flex">
                          <div className="d-flex align-items-center">
                            <img alt="activity" src={aerobicIcon} className="history-icon" />
                          </div>
                          <div>
                            <p className="history-name">{notes.name}</p>
                            <p className="history-index">{notes.caloriesBurned} calories</p>
                          </div>
                        </div>
                      ) : null}
                    </Link>
                  </div>
                );
              })}
              {notesData.length > displayedItems ? (
                <div className="d-flex justify-content-center">
                  <button className="show-btn" onClick={showMoreItems}>
                    Show More
                  </button>
                </div>
              ) : null}
            </div>
          ) : (
            <div>
              <h3 className="history-text">No notes data yet...</h3>
              <Link to="/allexcercisepage">
                <button className="home-btn">Add notes</button>
              </Link>
            </div>
          )}
          <Footer />
        </div>
      </div>
    </div>
  );
}
