import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import CustomNavbar from '../components/CustomNavbar';
import activityIcon from '../images/activity.png';
import expenseIcon from '../images/expense.png';
import officelogIcon from '../images/officelog.png';
import axios from 'axios';

function formatDate(dateString) {
  const options = { year: 'numeric', month: 'long', day: 'numeric' };
  return new Date(dateString).toLocaleDateString(undefined, options);
}

export default function SingleNote({ user }) {
  const { id, type } = useParams();
  const [noteData, setNoteData] = useState({});
  const navigate = useNavigate();

  const [isEditing, setIsEditing] = useState(false);
  const [editedNote, setEditedNote] = useState({
    name: '',
    description: '',
    amount: '',
    section: '',
  });

  useEffect(() => {
    const fetchNoteData = async () => {
      try {
        const userResponse = await axios.get(`https://fitnesslogger-backend.onrender.com/users/userId`);
        const userData = userResponse.data.data;

        if (userData.length > 0) {
          const fetchedUserId = user._id;

          const apiEndpoint =
            type === 'activity'
              ? `https://fitnesslogger-backend.onrender.com/aerobic/getActivity/${fetchedUserId}`
              : type === 'expense'
                ? `https://fitnesslogger-backend.onrender.com/cardio/getExpense/${fetchedUserId}`
                : type === 'officelog'
                  ? `https://fitnesslogger-backend.onrender.com/resistance/getOfficelog/${fetchedUserId}`
                  : null;

          if (apiEndpoint) {
            const noteResponse = await axios.get(apiEndpoint);
            const noteData = noteResponse.data;

            if (noteData.length > 0) {
              const targetNote = noteData.find((note) => note._id === id);

              if (targetNote) {
                setNoteData(targetNote);

                setEditedNote({
                  name: targetNote.name,
                  description: targetNote.description || '',
                  amount: targetNote.amount || '',
                  section: targetNote.section || '',
                });
              }
            }
          }
        } else {
          // console.error('No user data found.');
        }
      } catch (error) {
        // console.error('Error fetching data:', error);
      }
    };

    fetchNoteData(); // Call the fetchNoteData function
  }, [type, id, user._id]);

  const handleDeleteNote = async () => {
    try {
      const apiEndpoint =
        type === 'activity'
          ? `https://fitnesslogger-backend.onrender.com/aerobic/deleteActivity/${id}`
          : type === 'expense'
            ? `https://fitnesslogger-backend.onrender.com/cardio/deleteExpense/${id}`
            : type === 'officelog'
              ? `https://fitnesslogger-backend.onrender.com/resistance/deleteOfficelog/${id}`
              : null;

      if (apiEndpoint) {
        const response = await axios.delete(apiEndpoint);

        if (response.status === 200) {
          // console.log(`${type} note deleted successfully`);
          navigate('/history/:userId');
        } else {
          // console.error(`Failed to delete ${type} note`);
        }
      }
    } catch (error) {
      // console.error(`Error deleting ${type} note:`, error);
    }
  };

  const handleEditNote = () => {
    setIsEditing(true);
  };

  const handleSaveNote = async () => {
    try {
      const apiEndpoint =
        type === 'activity'
          ? `https://fitnesslogger-backend.onrender.com/aerobic/updateActivity/${id}`
          : type === 'expense'
            ? `https://fitnesslogger-backend.onrender.com/cardio/updateExpense/${id}`
            : type === 'resistance'
              ? `https://fitnesslogger-backend.onrender.com/resistance/updateOfficelog/${id}`
              : null;

      if (apiEndpoint) {
        const updatedNoteData = {
          ...noteData,
          name: editedNote.name,
          description: editedNote.description,
          amount: editedNote.amount,
          section: editedNote.section,
       
        };

        await axios.patch(apiEndpoint, updatedNoteData);

        setNoteData(updatedNoteData);
        setIsEditing(false);
      }
    } catch (error) {
      // console.error(`Error updating ${type} note:`, error);
    }
  };

  return (
    <div className={type === 'activity' ? 'single-activity' : type === 'expense' ? 'single-expense' : 'single-officelog'}>
      <CustomNavbar />
      <h2 className="title text-center">Note Details</h2>
      <div className="single-note d-flex flex-column align-items-center text-center">
        {type === 'activity' && Object.keys(noteData).length > 0 && (
          <div className="activity-div">
            {isEditing ? (
              <div>
                <input
                  type="number"
                  placeholder="Heart Rate"
                  value={editedNote.heartRate}
                  onChange={(e) => setEditedNote({ ...editedNote, heartRate: e.target.value })}
                />
                <input
                  type="number"
                  placeholder="Calories Burned"
                  value={editedNote.caloriesBurned}
                  onChange={(e) => setEditedNote({ ...editedNote, caloriesBurned: e.target.value })}
                />
                <button className="save-btn" onClick={handleSaveNote}>
                  Save Note
                </button>
              </div>
            ) : (
              <div>
                <img alt="activity" src={activityIcon} className="note-form-icon" />
                <p>
                  <span>Date: </span> {formatDate(noteData.date)}
                </p>
                <p>
                  <span>Name: </span> {noteData.name}
                </p>
                <p>
                  <span>Description: </span> {noteData.description} beats
                </p>
               
                <button className="edit-btn" onClick={handleEditNote}>
                  Edit Note
                </button>
                <button className="delete-btn" onClick={handleDeleteNote}>
                  Delete Note
                </button>
              </div>
            )}
          </div>
        )}

        {type === 'expense' && Object.keys(noteData).length > 0 && (
          <div className="expense-div">
            {isEditing ? (
              <div>
                <input
                  type="text"
                  placeholder="Note Name"
                  value={editedNote.name}
                  onChange={(e) => setEditedNote({ ...editedNote, name: e.target.value })}
                />
                <input
                  type="number"
                  placeholder="Distance (miles)"
                  value={editedNote.distance}
                  onChange={(e) => setEditedNote({ ...editedNote, distance: e.target.value })}
                />
                <input
                  type="number"
                  placeholder="Duration (minutes)"
                  value={editedNote.duration}
                  onChange={(e) => setEditedNote({ ...editedNote, duration: e.target.value })}
                />
                <button className="save-btn" onClick={handleSaveNote}>
                  Save Note
                </button>
              </div>
            ) : (
              <div>
                <img alt="expense" src={expenseIcon} className="note-form-icon" />
                <p>
                  <span>Date: </span> {formatDate(noteData.date)}
                </p>
                <p>
                  <span>Name: </span> {noteData.name}
                </p>
                <p>
                  <span>Amount: </span> {noteData.amount} Rs
                </p>
                <p>
                  <span>Discription: </span> {noteData.duration} minutes
                </p>
                <button className="edit-btn" onClick={handleEditNote}>
                  Edit Note
                </button>
                <button className="delete-btn" onClick={handleDeleteNote}>
                  Delete Note
                </button>
              </div>
            )}
          </div>
        )}

        {type === 'officelog' && Object.keys(noteData).length > 0 && (
          <div className="officelog-div">
            {isEditing ? (
              <div>
                <input
                  type="text"
                  placeholder="Note Name"
                  value={editedNote.name}
                  onChange={(e) => setEditedNote({ ...editedNote, name: e.target.value })}
                />
                <input
                  type="number"
                  placeholder="Weight (lbs)"
                  value={editedNote.weight}
                  onChange={(e) => setEditedNote({ ...editedNote, weight: e.target.value })}
                />
                <input
                  type="number"
                  placeholder="Sets"
                  value={editedNote.sets}
                  onChange={(e) => setEditedNote({ ...editedNote, sets: e.target.value })}
                />
                <input
                  type="number"
                  placeholder="Reps"
                  value={editedNote.reps}
                  onChange={(e) => setEditedNote({ ...editedNote, reps: e.target.value })}
                />
                <button className="save-btn" onClick={handleSaveNote}>
                  Save Note
                </button>
              </div>
            ) : (
              <div>
                <img alt="officelog" src={officelogIcon} className="note-form-icon" />
                <p>
                  <span>Date: </span> {formatDate(noteData.date)}
                </p>
                <p>
                  <span>Name: </span> {noteData.name}
                </p>
                <p>
                  <span>Section: </span> {noteData.section} lbs
                </p>
                <p>
                  <span>Description: </span> {noteData.description}
                </p>
              
                <button className="edit-btn" onClick={handleEditNote}>
                  Edit Note
                </button>
                <button className="delete-btn" onClick={handleDeleteNote}>
                  Delete Note
                </button>
              </div>
            )}
          </div>
        )}
      </div>
    </div>
  );
}
