import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import CustomNavbar from '../components/CustomNavbar';
import axios from 'axios';
import notesIcon from '../images/notes.png'; 
import expenseIcon from '../images/expense.png';
import officelogIcon from '../images/officelog.png'; 
import Footer from './Footer';

export default function History({ user }) {
  const userId = user._id;
  // console.log(userId);

  const [notesData, setNotesData] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);
  const [displayedItems, setDisplayedItems] = useState(10);

  useEffect(() => {
    const fetchNotesData = async () => {
      try {
        const notesResponse = await axios.get(`https://fitnesslogger-backend.onrender.com/notes/getNotes/${userId}`);
        // console.log('Notes Data:', notesResponse.data);

        const expenseResponse = await axios.get(`https://fitnesslogger-backend.onrender.com/expense/getExpense/${userId}`);
        // console.log('Expense Data:', expenseResponse.data);

        const officelogResponse = await axios.get(`https://fitnesslogger-backend.onrender.com/officelog/getOfficelog/${userId}`);
        // console.log('Officelog Data:', officelogResponse.data);

        const combinedNotesData = [
          ...(notesResponse.data || []),
          ...(Array.isArray(expenseResponse.data) ? expenseResponse.data : [expenseResponse.data]),
          ...(Array.isArray(officelogResponse.data) ? officelogResponse.data : [officelogResponse.data]),
        ];

        setNotesData(combinedNotesData);
        setIsLoading(false);
      } catch (error) {
        console.error('Error fetching notes data:', error);
        setError(error);
        setIsLoading(false);
      }
    };

    fetchNotesData();
  }, [userId]);

  function showMoreItems() {
    setDisplayedItems(displayedItems + 3);
  }

  if (isLoading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div>Error: {error.message}</div>;
  }
  return (
    
    <div className="history">
    <div className="history-page">
     <CustomNavbar style={{ color: 'blue' }} />

      <div className="d-flex flex-column align-items-center">
        <h2 className="title">History</h2>
        {notesData.length ? (
          <div className="history-data">
          {notesData.slice(0, displayedItems).map((note) => {
                let dateToDisplay = notesData.date;
              return (
                <div className="history-div d-flex" key={notes._id}>
                  <div className="date d-flex align-items-center">{dateToDisplay}</div>
                  <Link className="text-decoration-none" to={`/history/${notes.type}/${notes._id}`}>
                    {notes.type === 'activity' ? (
                      <div className="history-card activity-title d-flex">
                        <div className="d-flex align-items-center">
                          <img alt="activity" src={activityIcon} className="history-icon" />
                        </div>
                        <div>
                          <p className="history-name">{notes.name}</p>
                          <p className="history-index">{notes.description} miles</p>
                        </div>
                      </div>
                    ) : notes.type === 'expense' ? (
                      <div className="history-card expense-title d-flex">
                        <div className="d-flex align-items-center">
                          <img alt="expense" src={expenseIcon} className="history-icon" />
                        </div>
                        <div>
                          <p className="history-name">{notes.name}</p>
                          <p className="history-index">{notes.amount} Rs</p>
                        </div>
                      </div>
                    ) : notes.type === 'officelog' ? (
                      <div className="history-card officelog-title d-flex">
                        <div className="d-flex align-items-center">
                          <img alt="aerobic" src={officelogIcon} className="history-icon" />
                        </div>
                        <div>
                          <p className="history-name">{notes.name}</p>
                          <p className="history-index">{notes.section} dept</p>
                        </div>
                      </div>
                    ) : null}
                  </Link>
                </div>
              );
            })}
            {notesData.length > displayedItems ? (
              <div className="d-flex justify-content-center">
                <button className="show-btn" onClick={showMoreItems}>
                  Show More
                </button>
              </div>
            ) : null}
          </div>
        ) : (
          <div>
            <h3 className="history-text">No notes data yet...</h3>
            <Link to="/allnotespage">
              <button className="home-btn">Add notes</button>
            </Link>
          </div>
          
        )}
        <Footer />
      </div>
     
      </div>
      
    </div>
  );
}
