import React, { useState } from 'react';
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';
import activityIcon from '../images/aerobic.png';
import axios from 'axios';
import CustomNavbar from '../components/CustomNavbar';

const Activity = ({ user }) => {
  // console.log(user)
  const [activityForm, setActivityForm] = useState({
    name: '',
    date: new Date(),
    description,
    
    
  });
  const [message, setMessage] = useState('');

  const handleActivityChange = (event) => {
    const { name, value } = event.target;
    setActivityForm({ ...activityForm, [name]: value });
  };

  const handleDateChange = (date) => {
    setActivityForm({ ...activityForm, date });
  };

  const validateForm = (form) => {
    return (
      form.name &&
      form.date &&
      form.description 
    );
  };

  const handleActivitySubmit = async (event) => {
    event.preventDefault();

    const userId = user ? user._id : null;

    const dataToSend = {
      ...activityForm,
      userId: userId,
    };

    if (validateForm(activityForm)) {
      try {
        const response = await axios.post('https://fitnesslogger-backend.onrender.com/aerobic/createAerobic', dataToSend);

        if (response.status === 200) {
          setMessage('Activity notes successfully added');
          setTimeout(() => {
            setMessage('');
          }, 3000);
        } else {
          setMessage('Something else went wrong');
        }
      } catch (err) {
        setMessage('Something went wrong');
      }
    } else {
      setMessage('Please fill in all required fields.');
    }

    setActivityForm({
      name: '',
      date: new Date(),
      description: '', 
    });
  };

  return (
    <div className="activity">
      <CustomNavbar />
      <div className="d-flex flex-column align-items-center">
        <h2 className="title text-center">Add Activity Notes</h2>
        <form className="activity-form d-flex flex-column" onSubmit={handleActivitySubmit}>
          <div className="d-flex justify-content-center">
            <img alt="activity" src={activityIcon} className="notes-form-icon" />
          </div>
          <label>Name:</label>
          <input
            type="text"
            name="name"
            id="name"
            placeholder="name"
            value={activityForm.name}
            onChange={handleActivityChange}
          />
        <label>Date:</label>
          <DatePicker
            selected={aerobicForm.date}
            onChange={handleDateChange}
            placeholderText="mm/dd/yyyy"
          />
        
          <label>Description:</label>
          <input
            type="text"
            name="description"
            id="description"
            placeholder="type here"
            value={activityForm.description}
            onChange={handleActivityChange}
          />
          
          <button
            className="submit-btn aerobic-submit-btn"
            type="submit"
            disabled={!validateForm(activityForm)}
          >
            Add
          </button>
        </form>
        <p className="message">{message}</p>
      </div>
    </div>
  );
};

export default Activity;
